DROP FUNCTION IF EXISTS query_my_ratings(TEXT, INTEGER, TEXT);CREATE OR REPLACE FUNCTION query_my_ratings(_my_id TEXT, _tour_id INTEGER, _guide_id TEXT)	RETURNS TABLE(t_rating REAL, g_rating REAL) AS$$BEGIN	RETURN QUERY 	SELECT rate2tour.t_rating, rate2guide.g_rating    FROM rate2tour, rate2guide	WHERE rate2tour.u_id = _my_id::uuid AND rate2tour.t_id = _tour_id	AND rate2guide.u_id = _my_id::uuid AND rate2guide.g_id = _guide_id::uuid;	RETURN;END;$$ LANGUAGE 'plpgsql';