DROP FUNCTION IF EXISTS query_my_reservations(TEXT);CREATE OR REPLACE FUNCTION query_my_reservations(_my_id TEXT)	RETURNS SETOF view_my_reservations AS$$BEGIN	RETURN QUERY 	SELECT reservations.r_occupied, reservations.r_active, slots.s_id, slots.u_id, slots.s_date, slots.s_time, slots.s_capacity, slots.s_total_capacity, slots.s_active, slots.s_canceled, tours.t_id, tours.t_osm_id, tours.m_id, tours.t_title, tours.t_duration, tours.t_language, tours.t_location, tours.t_rating, tours.t_available, tours.t_description, locations.display_name, locations.type, locations.lat, locations.lon, users.u_name, users.email, users.u_rating	FROM reservations, slots, tours, locations, users	WHERE reservations.u_id = _my_id::uuid AND reservations.s_id = slots.s_id AND slots.t_id = tours.t_id AND slots.u_id = users.u_id	GROUP BY reservations.u_id, reservations.s_id, slots.s_id, tours.t_id, locations.display_name, locations.type, locations.lat, locations.lon, users.u_name, users.email, users.u_rating	ORDER BY slots.s_date ASC, slots.s_time ASC;	RETURN;END;$$ LANGUAGE 'plpgsql';