DROP FUNCTION IF EXISTS query_reservations_by_slot_id(BIGINT);CREATE OR REPLACE FUNCTION query_reservations_by_slot_id(_slot_id BIGINT)	RETURNS SETOF view_reservations AS$$BEGIN	RETURN QUERY 	SELECT users.u_id, users.u_name, users.email, users.u_rating, reservations.r_occupied    FROM users, reservations	WHERE reservations.s_id = _slot_id AND reservations.u_id = users.u_id AND reservations.r_active = B'1';	RETURN;END;$$ LANGUAGE 'plpgsql';