DROP FUNCTION IF EXISTS query_slots_by_guide_id(TEXT);CREATE OR REPLACE FUNCTION query_slots_by_guide_id(_guide_id TEXT)	RETURNS SETOF view_slots_guide AS$$BEGIN	RETURN QUERY 	SELECT slots.s_id, slots.s_date, slots.s_time, slots.s_capacity, slots.s_total_capacity, s_active, slots.s_canceled, tours.t_id, tours.t_osm_id, tours.m_id, tours.t_title, tours.t_duration, tours.t_language, tours.t_location, tours.t_rating, tours.t_description, locations.display_name, locations.type, locations.lat, locations.lon    FROM slots, tours, locations	WHERE slots.u_id = _guide_id::uuid AND slots.t_id = tours.t_id	GROUP BY slots.s_id, tours.t_id, locations.display_name, locations.type, locations.lat, locations.lon	ORDER BY slots.s_date ASC, slots.s_time ASC;	RETURN;END;$$ LANGUAGE 'plpgsql';