DROP FUNCTION IF EXISTS query_tours_by_manager_id(TEXT);CREATE OR REPLACE FUNCTION query_tours_by_manager_id(_manager_id TEXT)	RETURNS SETOF view_tours_locations AS$$BEGIN	RETURN QUERY 	SELECT tours.t_id, tours.t_osm_id, tours.t_title, tours.t_duration, tours.t_language, tours.t_location, tours.t_rating, tours.t_description,			locations.display_name, locations.type, locations.lat, locations.lon	FROM tours, locations	WHERE tours.u_id = _manager_id::uuid AND tours.t_osm_id = locations.osm_id AND tours.t_available = B'1'	ORDER BY tours.t_rating DESC;	RETURN;END;$$ LANGUAGE 'plpgsql';